mapper.insert:

    SELECT S_USERS.NEXTVAL FROM DUAL

INSERT INTO USERS (
    ID, LOGIN_ID, PASSWORD, NAME, PHONE, EMAIL, ADDRESS, BIRTH_DATE, ROLE, STATUS, EMAIL_VERIF_STATUS, MEM_IMG, WITHDRAWAL_AT, CREATED_AT, UPDATED_AT
) VALUES (
    #{id}, #{loginId}, #{password}, #{name}, #{phone}, #{email}, #{address}, #{birthDate}, #{role}, #{status}, #{emailVerifStatus}, #{memImg}, #{withdrawalAt}, SYSTIMESTAMP, #{updatedAt}
)

mapper.delete:
DELETE FROM USERS
WHERE STATUS = 'PENDING_WITHDRAWAL'
  AND WITHDRAWAL_AT <= #{now}
