mapper.insert:
INSERT INTO USER_PROFILES (
    USER_ID, NICKNAME, AVATAR_URL, TOTAL_WATER_SAVED_L, TOTAL_CARBON_SAVED_KG,
    ECO_POINTS, TOTAL_COLLECTED_KG, TOTAL_CLOTHING_COUNT, LAST_LOGIN_AT, GENDER, STYLE_PREFERENCE,
    ACTIVE_DECORATION
) VALUES (
    #{userId}, #{nickname}, #{avatarUrl}, #{totalWaterSavedL}, #{totalCarbonSavedKg},
    #{ecoPoints}, #{totalCollectedKg}, #{totalClothingCount}, #{lastLoginAt}, #{gender}, #{stylePreference},
    #{activeDecoration}
)

mapper.update:
UPDATE USER_PROFILES
SET LAST_LOGIN_AT = #{lastLoginAt}
WHERE USER_ID = #{userId}
